// ðŸ“„ src/pages/LivePricesPage.jsx
import React, { useEffect, useState } from 'react';
import { getMarketPrices } from '../api/marketApi';

const LivePricesPage = () => {
    const [prices, setPrices] = useState({});

    useEffect(() => {
        const fetchPrices = () => {
            getMarketPrices().then(res => setPrices(res.data));
        };

        fetchPrices(); // initial fetch
        const interval = setInterval(fetchPrices, 1000); // update every 5 seconds

        return () => clearInterval(interval); // cleanup on unmount
    }, []);

    return (
        <div className="page-container">
            <h1>Live Cryptocurrency Prices</h1>
            <ul>
                {Object.entries(prices).map(([currency, price]) => (
                    <li key={currency}>{currency}: ${price.toFixed(5)}</li>
                ))}
            </ul>
        </div>
    );
};

export default LivePricesPage;
