// ðŸ“„ src/pages/LivePricesPage.jsx
import { useEffect, useState } from 'react';
import { getMarketPrices } from '../api/marketApi';
import { Link } from 'react-router-dom';

const LivePricesPage = () => {
  const [prices, setPrices] = useState({});

  useEffect(() => {
    const fetchPrices = () => {
      getMarketPrices().then(res => setPrices(res.data));
    };

    fetchPrices(); // initial fetch
    const interval = setInterval(fetchPrices, 1); // update every 5 seconds

    return () => clearInterval(interval); // cleanup on unmount
  }, []);

  return (
    <div className="prices-container">
      <nav className="nav-bar">
        <ul className="nav-list">
          <li><Link to="/">Dashboard</Link></li>
          <li><Link to="/buy-sell">Buy/Sell</Link></li>
          <li><Link to="/history">History</Link></li>
          <li><Link to="/admin">Admin</Link></li>
          <li><Link to="/live-prices" className="active">Live Prices</Link></li>
        </ul>
      </nav>

      <div className="prices-content">
        <h1 className="prices-title">ðŸ“ˆ Live Cryptocurrency Prices</h1>
        <div className="price-grid">
          {Object.entries(prices).map(([currency, price]) => (
            <div key={currency} className="price-card">
              <h2>{currency}</h2>
              <p>${price.toFixed(2)}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default LivePricesPage;
