import React, { useState, useEffect } from 'react';
import { buyCrypto, sellCrypto } from '../api/transactionApi';
import { getSupportedPairs } from '../api/marketApi';

const BuySellForm = ({ userId }) => {
    const [currency, setCurrency] = useState('');
    const [amount, setAmount] = useState('');
    const [pairs, setPairs] = useState([]);

    useEffect(() => {
        getSupportedPairs().then(res => {
            setPairs(res.data.map(pair => pair.split('/')[0]));
        });
    }, []);

    const handleBuy = () => {
        buyCrypto(userId, currency, amount).then(() => {
            alert("Bought successfully!");
        });
    };

    const handleSell = () => {
        sellCrypto(userId, currency, amount).then(() => {
            alert("Sold successfully!");
        });
    };

    return (
        <div>
            <h2>Trade Crypto</h2>
            <select value={currency} onChange={e => setCurrency(e.target.value)}>
                <option value="">Select currency</option>
                {pairs.map(pair => <option key={pair} value={pair}>{pair}</option>)}
            </select>
            <input type="number" value={amount} onChange={e => setAmount(e.target.value)} />
            <button onClick={handleBuy}>Buy</button>
            <button onClick={handleSell}>Sell</button>
        </div>
    );
};

export default BuySellForm;
